import Button from "../UI/ Button/Button";
import axios from "axios";
import {OrderContext} from "../../context/OrderContext";
import {useContext, useState} from "react";

const Product = (props) => {
    const item = props.item;
    const products = props.products;

    const {orderId, setUpdate} = useContext(OrderContext);
    const [replace, setReplace] = useState(false)
    const clickHandler = async (e) => {
        e.preventDefault()
        console.log('Product was added to cart...', item.id)
        console.log('Selected Order Id: ', orderId)
        item.qty = item.qty ?? 1;

        const options = {
            products: [item]
        };

        try {
            const response = await axios.post(`http://localhost:3001/api/orders/${orderId}/products`, options);

            //debugger;
            setUpdate(true);
            //setOrders(response.data);

            //console.log('My Order:', orders);
            //console.log('Response:', response.data);
            //setOrderId(order.id)

        } catch (error) {
            console.error('Error fetching data:', error);
        }
    }

    const replaceHandler = async (e) => {
        e.preventDefault()
        debugger;
        console.log('Product was added to cart...', item.id)
        console.log('Selected Order Id: ', orderId)
        item.qty = item.qty ?? 1;

        const options = {
            replace_with: [item.replace_with]
        };

        try {
            debugger;
            const response = await axios.patch(`http://localhost:3001/api/orders/${orderId}/products/${item.id}`, options);

            //debugger;
            setUpdate(true);
            //setOrders(response.data);

            //console.log('My Order:', orders);
            //console.log('Response:', response.data);
            //setOrderId(order.id)

        } catch (error) {
            console.error('Error fetching data:', error);
        }
    }

    const inputHandler = (e) => {
        item.qty = parseInt(e.target.value);
    }

    const replaceWithHandler = (e) => {
        let val = parseInt(e.target.value);
        let replacement = products.find(product => product.id === val);

        if (replacement && item.id != val) {
            setReplace(true);
            item.replace_with = {"product_id": val, "quantity": item.qty} ?? 1;
        } else {
            setReplace(false);
            delete item.replace_with;
        }
    }

    return (
        <li>
            <span>ProductID: {item.id}</span>
            <span>Name: {item.name}</span>
            <span>Price: {item.price}</span>
            <span>Qty: <input onChange={inputHandler} defaultValue="1" type="number" /></span>
            <span>Replace With: <input onChange={replaceWithHandler} type="number" /></span>
            {!replace && <span><Button title="Add To Order" clickHandler={clickHandler} /></span>}
            {replace && <span><Button title="Replace Item" clickHandler={replaceHandler} /></span>}
        </li>
    );
}

export default Product;
