import React, {useContext, useState, useEffect} from 'react';
import Order from "./orders/Order";
import {OrderContext} from "../context/OrderContext";
import Button from "./UI/ Button/Button";
import axios from "axios";

const Orders = () => {
    const {setOrderId, setUpdate} = useContext(OrderContext);
    const [orders, setOrders] = useState([]);

    const createOrder = async () => {
        try {
            //debugger;
            const response = await axios.post(`${process.env.REACT_APP_BE_URL}/api/orders`);
            if (response.data) {
                orders.push(response.data);
                setOrders(orders);
                setOrderId(response.data.id);
                setUpdate(true);
            }

        } catch (error) {
            console.error('Error creating order:', error);
        }
    };

    useEffect(() => {
        createOrder();
    }, []);

    const updateOrder = async (updatedOrder) => {
        try {
            orders.map(order => {
                if (order.id === updatedOrder.id) {
                    order.status = updatedOrder.status;
                }
            })

        } catch (error) {
            console.error('Error creating order:', error);
        }
    };

  return (
      <div>
          <div className="create-order">
              <Button title="Create New Order" clickHandler={createOrder} />
          </div>
          <ul className="order-container">
            {orders.length
                ? orders.map(order => <Order key={order.id} order={order} updateOrder={updateOrder} />)
                : 'Order collection is empty'
            }
          </ul>
      </div>
  );
}

export default Orders;
