import Button from "../UI/ Button/Button";
import axios from "axios";
import {OrderContext} from "../../context/OrderContext";
import {useContext} from "react";

const Product = (props) => {
    const item = props.item;
    const {orderId, setOrderId, orders, setOrders} = useContext(OrderContext);

    const clickHandler = async () => {
        console.log('Product was added to cart...', item.id)
        console.log('Selected Order Id: ', orderId)

        try {
            const options = {
                id: orderId,
                items: [item],
                status: 'UPDATED'
            };
            const response = await axios.patch('http://localhost:3001/api/orders/' + orderId, options);
            const orderOnServer = orders.find(order => order.id === orderId);
            const order = orders.find(order => order.id === orderId);
            debugger;
            setOrders(prev => ({...prev, status: order.status, items: order.items}))
            //setOrders({...this.state.jasper, name: 'someothername'})
            console.log('My Order:', orderOnServer);
            console.log('Response:', response.data);
            setOrderId(response.data.id)

        } catch (error) {
            console.error('Error fetching data:', error);
        }
    }

    return (
        <div>
            <p>{item.id} . {item.name}  - {item.price}</p>
            <Button title="Submit" clickHandler={clickHandler} />
        </div>
    );
}

export default Product;
