import Button from "../UI/ Button/Button";
import axios from "axios";
import {OrderContext} from "../../context/OrderContext";
import {useContext} from "react";

const Product = (props) => {
    const item = props.item;
    const {orderId, setOrderId, orders, setOrders} = useContext(OrderContext);

    const clickHandler = async () => {
        console.log('Product was added to cart...', item.id)
        console.log('Selected Order Id: ', orderId)

        try {
            const options = {
                id: orderId,
                items: [item],
                qty: 1,
                status: 'UPDATED'
            };

            const response = await axios.patch('http://localhost:3001/api/orders/' + orderId, options);
            //debugger;

            //const order = orders.find(order => order.id === orderId);
            //order.status = newOrder.status;
            //order.items = newOrder.items;
            //const newOrder = response.data;

            //prev.map(order => order.id == newOrder.id ? newOrder : order)
            setOrders(response.data);

            //setOrders(prev => ({...prev, items: newOrder.items}))
            console.log('My Order:', orders);
            console.log('Response:', response.data);
            //setOrderId(order.id)

        } catch (error) {
            console.error('Error fetching data:', error);
        }
    }

    const inputHandler = (e) => {
        debugger;
        item.qty = e.target.value;
    }

    return (
        <li>
            <span>ProductID: {item.id}</span>
            <span>Name: {item.name}</span>
            <span>Price: {item.price}</span>
            <span>Qty: <input onChange={(e) => inputHandler(e)} value={item.qty} type="number" /></span>
            <span><Button title="Add To Order" clickHandler={clickHandler} /></span>
        </li>
    );
}

export default Product;
