import Button from "../UI/ Button/Button";
import axios from "axios";

const Product = (props) => {
    const item = props.item;

    const clickHandler = async () => {
        console.log('Product was added to cart...', item.id)

        try {

            const options = {
                "da": "0"
            }
            const response = await axios.post('http://localhost:3001/api/orders/:order_id/products', options);
            console.log('Response:', response.data.products);
        } catch (error) {
            console.error('Error fetching data:', error);
        }
    }

    return (
        <div>
            <p>{item.id} . {item.name}  - {item.price}</p>
            <Button title="Submit" clickHandler={clickHandler} />
        </div>
    );
}

export default Product;
