import React, {useContext, useEffect, useState} from 'react';
import {OrderContext} from "../context/OrderContext";
import Item from "./orders/Item";
import axios from "axios";

const CurrentOrder = () => {
    const [, order, setOrder] = useState(false)
    const {orders, orderId} = useContext(OrderContext);

    debugger;
    if (!orders.length) {
        return null;
    }
/*
    const getOrder = async () => {
        try {
            debugger;
            const response = await axios.get(`http://localhost:3001/api/orders/${orderId}`);
            setOrder(response.data);
        } catch (error) {
            debugger;
            console.error('Error creating order:', error);
        }
    }


    getOrder()*/


    const getOrder = async (orderId) => {
        orderId = orders[0].id;
        try {
            debugger;
            const response = await axios.get(`http://localhost:3001/api/orders/${orderId}`);
            if (response.data) {
                setOrder(response.data);
            }

        } catch (error) {
            console.error('Error creating order:', error);
        }
    }

    getOrder(orderId);


    debugger;

    return (
        <>
            {order &&
             <div className="current-order">
                 <div className="order-element">Currently Used Order ID: {order.id}</div>
                 <div className="order-element">Status: {order.status}</div>
                 <div className="order-element">Items: {order.products.length}</div>
                 <div className="order-item-container">
                     {order.products.length
                         ? order.items.map(item => <Item key={item.id} item={item} />)
                         : 'No Items'
                     }
                 </div>
             </div>}
        </>
    );
}

export default CurrentOrder;
