import {useEffect, useContext} from "react";
import {OrderContext} from "../../context/OrderContext";
import axios from "axios";

export function GetOrders() {
    const {setOrders, setOrderId, orders} = useContext(OrderContext);
    const GetOrders = async () => {
        try {
            debugger;
            const response = await axios.get('http://localhost:3001/api/orders/collection');
            if (response.data) {
                setOrders(response.data);
                setOrderId(response.data.length ? response.data[0].id : false);
            } else {
                const response = await axios.post('http://localhost:3001/api/orders');
                if (response.data) {
                    orders.push(response.data);
                    setOrders(orders);
                    setOrderId(response.data.id);
                }
            }

        } catch (error) {
            console.error('Error creating order:', error);
        }
    };

    useEffect(() => {
        GetOrders();
    }, []);
}
