import Button from "../UI/ Button/Button";
import axios from "axios";
import {OrderContext} from "../../context/OrderContext";
import {useContext} from "react";

const Product = (props) => {
    const item = props.item;
    //(const {orderId, setOrderId, orders, setOrders} = useContext(OrderContext);
    const {orderId, orders} = useContext(OrderContext);

    const clickHandler = async (e) => {
        e.preventDefault()
        console.log('Product was added to cart...', item.id)
        console.log('Selected Order Id: ', orderId)

        const options = {
            orderId: orderId,
            products: item
        };

        try {
            const response = await axios.post(`http://localhost:3001/api/orders/${orderId}/products`, options);
            debugger;
            //setOrders(response.data);

            console.log('My Order:', orders);
            console.log('Response:', response.data);
            //setOrderId(order.id)

        } catch (error) {
            console.error('Error fetching data:', error);
        }
    }

    const inputHandler = (e) => {
        item.qty = parseInt(e.target.value);
    }

    return (
        <li>
            <span>ProductID: {item.id}</span>
            <span>Name: {item.name}</span>
            <span>Price: {item.price}</span>
            <span>Qty: <input onChange={(e) => inputHandler(e)} defaultValue="1" type="number" /></span>
            <span><Button title="Add To Order" clickHandler={clickHandler} /></span>
        </li>
    );
}

export default Product;
