import React, { useContext, useEffect, useState } from 'react';
import { OrderContext } from "../context/OrderContext";
import Item from "./orders/Item";
import axios from "axios";
import {GetCurrentOrder} from "./hooks/GetCurrentOrder";

const CurrentOrder = () => {
    const { update, orderId, setUpdate} = useContext(OrderContext);
    //const { update, orderId, setUpdate } = useContext(OrderContext);
    const [order, setOrder] = useState(null);
    const [orderItems, setOrderItems] = useState(null);

    useEffect(() => {
        debugger;
        const orderData = GetCurrentOrder(orderId, setUpdate);

        setOrder(orderData.order)
        setOrderItems(orderData.orderItems)
        /*const getOrder = async () => {
            try {
                if (!orderId) {
                    return;
                }
                const response = await axios.get(`${process.env.REACT_APP_BE_URL}/api/orders/${orderId}`);
                if (response.data) {
                    setOrder(response.data);
                }

                const responseItems = await axios.get(`${process.env.REACT_APP_BE_URL}/api/orders/${orderId}/products`);
                if (responseItems.data) {
                    setOrderItems(responseItems.data);
                }
                setUpdate(false);
            } catch (error) {
                console.error('Error fetching order:', error);
            }
        }

        getOrder();
        */
    }, [update]);

    return (
        <>
            {order &&
             <div className="current-order">
                 <div className="order-element">Currently Used Order ID: {order.id}</div>
                 <div className="order-element">Status: <span class={order.status.toLowerCase()}>{order.status}</span></div>
                 <div className="order-element">Items: {orderItems ? orderItems.length : 0}</div>
                 <div className="order-item-container">
                     {orderItems && orderItems.length
                         ? orderItems.map(item => <Item key={item.id} item={item} />)
                         : 'No Items'
                     }
                 </div>
             </div>}
        </>
    );
}

export default CurrentOrder;
