import axios from "axios";

export function AddUpdateProduct(item, orderId, setUpdate) {
    const addUpdateProduct = async (e) => {
        debugger;

        e.preventDefault()
        item.qty = item.qty ?? 1;
        const options = {
            products: [item]
        };

        try {
            const response = await axios.post(`${process.env.REACT_APP_BE_URL}/api/orders/${orderId}/products`, options);
            setUpdate(true);
        } catch (error) {
            console.error('Error fetching data:', error);
        }
    }

    return addUpdateProduct;
}
