import {useContext, useState} from "react";
import axios from "axios";

export function GetCurrentOrder(orderId, setUpdate) {
    const [order, setOrder] = useState({'order': [], 'items': []});
debugger;

    const getOrderData = async () => {
        try {
            debugger;
            if (!orderId) {
                return;
            }
            const response = await axios.get(`${process.env.REACT_APP_BE_URL}/api/orders/${orderId}`);
            if (response.data) {
                //order.push({'order': response.data})
                setOrder(order.order.push(response.data));
                //setOrder(response.data);
            }

            const responseItems = await axios.get(`${process.env.REACT_APP_BE_URL}/api/orders/${orderId}/products`);
            if (responseItems.data) {
                //order.push({'orderItems': responseItems.data})
                setOrder(order.items.push(responseItems.data));
                //setOrderItems(responseItems.data)
                //setOrder(order);
            }
            setUpdate(false);
        } catch (error) {
            debugger;
            console.error('Error fetching order:', error);
        }

        return order;
    }

    return getOrderData
}
