import React, {useContext} from 'react';
import Order from "./orders/Order";
import {OrderContext} from "../context/OrderContext";
import {CreateOrder} from "../components/hooks/CreateOrder";
import Button from "./UI/ Button/Button";
import axios from "axios";

const Orders = () => {
    const {setOrders, orders, setOrderId} = useContext(OrderContext);
    const createOrder = async () => {
        try {
            debugger;
            const response = axios.post('http://localhost:3001/api/orders');
            if (response.data) {
                setOrders(response.data.orders);
                setOrderId(response.data.orderId);
            }

        } catch (error) {
            console.error('Error creating order:', error);
        }
    };

  return (
      <div>
          <div className="create-order">
              <Button title="Create New Order" clickHandler={() => createOrder()} />
          </div>
          <ul className="order-container">
            {orders.length
                ? orders.map(order => <Order key={order.id} order={order} />)
                : 'Order collection is empty'
            }
          </ul>
      </div>
  );
}

export default Orders;
