import React, {useContext} from 'react';
import Button from "../UI/ Button/Button";
import {OrderContext} from "../../context/OrderContext";
import axios from "axios";

const Order = ({order, updateOrder}) => {
    const {orderId, setOrderId, setUpdate} = useContext(OrderContext);
    const clickHandler = () => {
        setOrderId(order.id);
        setUpdate(true)
        //console.log('Selected Order Id From Orders: ', orderId)
    }
    const updateHandler = async () => {
        let options = {
            "status": "PAID"
        }

        try {
            debugger;
            const response = await axios.patch(`http://localhost:3001/api/orders/${order.id}`, options);
            console.log(response)
            setOrderId(response.data.id);
            setUpdate(true)
            updateOrder(response.data);
        } catch (error) {
            console.error('Error creating order:', error);
        }
    }

  return (
      <li className="order-element">
          <div>
              {orderId != order.id &&
               <div>
                   <Button title="Use this Order" clickHandler={clickHandler} />
                   {order.status != 'PAID' && <Button title="Update Order" clickHandler={updateHandler} />}
               </div>
              }
              Order ID: {order.id}
          </div>
      </li>
  );
}

export default Order;
