import React, {useContext} from 'react';
import Order from "./orders/Order";
import {OrderContext} from "../context/OrderContext";
import Button from "./UI/ Button/Button";
import axios from "axios";

const Orders = () => {
    const {setOrderId, orders, setOrders} = useContext(OrderContext);
    const createOrder = async () => {
        try {
            debugger;
            const response = await axios.post('http://localhost:3001/api/orders');
            if (response.data) {
                setOrders(response.data);
                setOrderId(response.data.id);
            }

        } catch (error) {
            console.error('Error creating order:', error);
        }
    };

    const clearOrder = async () => {
        try {
            debugger;
            const response = await axios.post('http://localhost:3001/api/orders/delete');
            setOrders(response.data.orders);
            setOrderId(response.data.orderId);
        } catch (error) {
            console.error('Error creating order:', error);
        }
    };

  return (
      <div>
          <div className="create-order">
              <Button title="Create New Order" clickHandler={createOrder} />
              <Button title="Clear Orders" clickHandler={clearOrder} />
          </div>
          <ul className="order-container">
            {orders.length
                ? orders.map(order => <Order key={order.id} order={order} />)
                : 'Order collection is empty'
            }
          </ul>
      </div>
  );
}

export default Orders;
